eria_imagePosition":"center","galleria_initialTransition":"slide","galleria_transition":"fade","galleria_trueFullscreen":"true","galleria_transitionSpeed":"400","galleria_pauseOnInteraction":"true","galleria_preload":"2","galleria_queue":"true","galleria_responsive":"true","galleria_show":"0","galleria_showCounter":"true","galleria_showInfo":"true","galleria_showImageNav":"true","galleria_swipe":"true","galleria_thumbnails":"true","galleria_thumbMargin":"0","galleria_thumbQuality":"true","slider_content_width":"100%","slider_content_height":"","slider_content_cols":3,"slider_content_readmore":"","slider_content_image":"1","slider_content_date":"1","layer_slider_width":"100%","layer_slider_height":"300px","layer_slider_cols":"1","layer_slider_effects":"breaking_news","layer_slider_layout":"_slide.php","layer_slider_html":"true","layer_slider_autoPlay":"true","layer_slider_autoPlayDelay":"3000","layer_slider_nav":"true","layer_slider_pagination":"true","module_tag":"div","bootstrap_size":"0","header_tag":"h3","header_class":"","style":"0"};
    setCols = function (contain, containerWidth, cols)
    {
        var widthWindow = $(window).width();

        if (widthWindow <= 320) {
            cols = wallParams.wall_grid_cols_320;
        } else if (widthWindow <= 480) {
            cols = wallParams.wall_grid_cols_480;
        } else if (widthWindow <= 767) {
            cols = wallParams.wall_grid_cols_767;
        }

        if (widthWindow <= 320) {
            itemWidth = containerWidth/wallParams.wall_grid_cols_320;
        } else if (widthWindow <= 480) {
            itemWidth = containerWidth/wallParams.wall_grid_cols_480;
        } else if (widthWindow <= 767){
            itemWidth = containerWidth/wallParams.wall_grid_cols_767;
        } else {
            itemWidth = containerWidth/cols;
        }

        $(contain + ' .item').each(function(el)
        {
            if (wallParams.wall_item_width == 'random')
            {
                //random Item Cosl 1->12
                wallParams.wall_item_width = Math.floor(Math.random() * (12 - 1 + 1)) + 1;
                // Item Cols can not bigger grid cols
                (wallParams.wall_item_width > cols) ? wallParams.wall_item_width = cols : '';
                $(this).css('width', itemWidth * wallParams.wall_item_width);
            }
            else
            {
                $(this).css('width', itemWidth * wallParams.wall_item_width);
            }
        });
        return cols;
    }

    $(document).ready(function(){
        var wrapper = $('#53e8c9cef13ba');
        var contain = wrapper.find('.layout-wall');
        contain.imagesLoaded(function(){
            contain.masonry({
                itemSelector : '.item',
                isAnimated: true,
                isResizable: true,
                columnWidth: function( containerWidth ) {
                    cols = setCols('#53e8c9cef13ba', containerWidth, wallParams.wall_grid_cols);
                    return containerWidth /cols;
                }
            });

            $(window).resize(function(){
                contain.masonry('reload');
                setTimeout(function(){
                    contain.css({'overflow' : 'visible'});
                }, 1000);
            });
        });

        // category
        wrapper.find('.category-wall span').each(function()
        {
            $(this).click(function()
            {
                var className = $(this).attr('class');
                if (className == 'all') {
                    className = 'item';
                }

                contain.find(' > div:not([class*="' + className + '"])').removeClass('displayBlock').addClass('displayNone');
                contain.find(' > div[class*="' + className + '"]').removeClass('displayNone').addClass('displayBlock');

                contain.masonry({
                    itemSelector : '.' + className,
                    isAnimated: true,
                    isResizable: true,
                    columnWidth: function( containerWidth ) {
                        cols = setCols('#53e8c9cef13ba', containerWidth, wallParams.wall_grid_cols);
                        return containerWidth /cols;
                    }
                });

                contain.masonry('reload');
                setTimeout(function(){
                    contain.css({'overflow' : 'visible'});
                }, 1000);
            });
        });
    });
})(jQuery);

